@import url("https://fonts.googleapis.com/css2?family=Open+Sans:wght@300;400&display=swap");
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: "Open Sans", Arial, Helvetica, sans-serif;
  min-height: 100vh;
  height: 100%;
  background: #3F3C50;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
}

.dashboard {
  max-width: 1300px;
  min-height: 90vh;
  width: 100%;
  height: 100%;
  background: #201D2B;
  color: #eee;
  border-radius: 15px;
  display: grid;
  grid-template-columns: 17vw auto;
}

.sidebar {
  position: fixed;
  width: 15vw;
  min-height: 90vh;
  border-top-left-radius: 15px;
  background: #292034;
}
.sidebar .heading {
  height: 100px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-top-left-radius: 15px;
}
.sidebar .heading .title {
  font-size: 1.25rem;
}
.sidebar .nav {
  display: flex;
  flex-direction: column;
  align-items: center;
}
.sidebar .nav .nav-menu {
  list-style: none;
}
.sidebar .nav .nav-menu .nav-item {
  margin: 1rem 0;
  display: flex;
}
.sidebar .nav .nav-menu .nav-item .nav-link {
  padding: 0.5rem;
  text-transform: capitalize;
  text-decoration: none;
  font-weight: bold;
  font-size: 1.15rem;
  color: #eee;
}
.sidebar .nav .nav-menu .nav-item .nav-link span {
  margin-right: 0.5rem;
}

.content-grid {
  grid-column-start: 2;
  height: 100%;
}

.header {
  height: 100px;
  border-top-right-radius: 15px;
  display: grid;
  grid-template-columns: 2fr 1fr 2fr;
  align-items: center;
}
.header .left input {
  border-radius: 7px;
  padding: 0.5rem;
  font-size: 1.05rem;
  background: #363341;
  border: 0;
  outline: 0;
  width: 100%;
  color: #eee;
}
.header .right {
  align-items: center;
  display: flex;
  justify-content: center;
}
.header .right .user-name {
  margin: 0 0.5rem;
}
.header .right .fa-angle-down {
  color: #777;
  margin-left: 0.25rem;
}
.header .right .fa-angle-down:hover {
  cursor: pointer;
}
.header .right .fa-bell {
  margin-left: 4rem;
  position: relative;
}
.header .right .fa-bell:hover {
  cursor: pointer;
}
.header .right .fa-bell::after {
  content: "";
